function getCookie(t){let o=null;if(document.cookie&&""!==document.cookie){const e=document.cookie.split(";");for(let n=0;n<e.length;n++){const c=e[n].trim();if(c.substring(0,t.length+1)===t+"="){o=decodeURIComponent(c.substring(t.length+1));break}}}return o}const csrftoken=getCookie("csrftoken");function addToCart(t,o,e){$.ajax({method:"post",url:"/cart/add-to-cart/",cache:!1,data:{id:t,title:e,price:o,quantity:1,csrfmiddlewaretoken:csrftoken},success:t=>{$("#cart-quan").html(t.length)}})}$(".container").hide(),$(window).on("load",function(){$("#spin").remove(),$(".container").show()});